  --shadow-sm: 0px 2px 0px 0px hsl(230 15% 15% / 0.04), 0px 1px 2px -1px hsl(230 15% 15% / 0.05);
  --shadow: 0px 2px 0px 0px hsl(230 15% 15% / 0.05), 0px 1px 2px -1px hsl(230 15% 15% / 0.06);
